CREATE TRIGGER `prevent_time_conflict` BEFORE INSERT ON `enrollment` FOR EACH ROW BEGIN
    DECLARE conflict_count INT;

    SELECT COUNT(*) INTO conflict_count
    FROM enrollment e
    JOIN section s1 ON e.section_id = s1.sectionID
    JOIN class_time ct1 ON s1.timeID = ct1.timeID
    JOIN section s2 ON s2.sectionID = NEW.section_id
    JOIN class_time ct2 ON s2.timeID = ct2.timeID
    WHERE e.user_id = NEW.user_id
      AND (
           -- Check overlapping days
           (ct1.monday AND ct2.monday) OR
           (ct1.tuesday AND ct2.tuesday) OR
           (ct1.wednesday AND ct2.wednesday) OR
           (ct1.thursday AND ct2.thursday) OR
           (ct1.friday AND ct2.friday) OR
           (ct1.saturday AND ct2.saturday) OR
           (ct1.sunday AND ct2.sunday)
          )
      AND (
           -- Check overlapping times
           ct1.time_start < ct2.time_end AND
           ct1.time_end > ct2.time_start
          );

    IF conflict_count > 0 THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Schedule conflict: Time overlap with another enrolled class.';
    END IF;
END
$$
